function scr_machroll () {
    if (13 == playersprite_index) {
        if (playerframecounter == 125) {
            if (!(controller.player1.isPressed(ControllerButton.Down))) {
                playerstate = 4
            }
        }
    } else {
        if (!(controller.player1.isPressed(ControllerButton.A)) && mySprite.isHittingTile(CollisionDirection.Bottom)) {
            playersprite_index = 12
        } else {
            if (!(mySprite.vy == 350)) {
                playersprite_index = 3
            } else {
                playersprite_index = 11
            }
        }
    }
    mySprite.vx = player_movespeed * playerxscale
    mySprite.setImage(assets.image`myImage3`)
    if (mySprite.isHittingTile(CollisionDirection.Bottom) && !(controller.player1.isPressed(ControllerButton.Down)) && !(tiles.tileAtLocationIsWall(tiles.getTileLocation(Math.floor((mySprite.x + 0) / 16), Math.floor((mySprite.y - 16) / 16))))) {
        if (player_movespeed < 300) {
            playerstate = 1
        } else {
            playerstate = 4
        }
    }
    if (mySprite.isHittingTile(CollisionDirection.Bottom) && controller.player1.isPressed(ControllerButton.A)) {
        mySprite.vy = -250
        if (!(player_ispepperman_Pixel)) {
            player_movespeed += 35
        }
        playerstate = 1
        if (player_ispepperman_Pixel) {
            music.play(music.createSoundEffect(WaveShape.Sine, 1, 598, 255, 0, 250, SoundExpressionEffect.Warble, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
        } else {
            music.play(music.createSoundEffect(WaveShape.Triangle, 400, 600, 255, 0, 250, SoundExpressionEffect.None, InterpolationCurve.Linear), music.PlaybackMode.InBackground)
        }
        music.play(music.createSoundEffect(WaveShape.Noise, 1, 508, 255, 0, 300, SoundExpressionEffect.None, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
        if (mySprite.isHittingTile(CollisionDirection.Bottom)) {
            playerstate = 4
        }
    }
    if (mySprite.isHittingTile(CollisionDirection.Bottom)) {
        if (controller.player1.isPressed(ControllerButton.Down)) {
            playersprite_index = 12
        } else {
            if (player_movespeed < 300) {
                playerstate = 1
            } else {
                playerstate = 4
                playersprite_index = 4
            }
        }
    } else {
        if (!(controller.player1.isPressed(ControllerButton.A))) {
            music.play(music.createSoundEffect(WaveShape.Sine, 878, 387, 255, 0, 500, SoundExpressionEffect.Vibrato, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
            playerstate = 18
            playergrav = 10
            playermove = 18
            if (player_movespeed < 340) {
                player_movespeed += 45
            } else {
                player_movespeed = 340
            }
        }
    }
    if (mySprite.isHittingTile(CollisionDirection.Left)) {
        if (playersprite_index == 3 || !(playersprite_index == 11)) {
            bonk()
        } else if (playersprite_index == 11 && 0 == player_isdoise_Coneguy) {
            if (!(player_ispepperman_Pixel)) {
                music.play(music.createSoundEffect(WaveShape.Square, 200, 1, 255, 0, 100, SoundExpressionEffect.None, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
                playerxscale = 1
                mySprite.vx = player_movespeed * playerxscale
            } else {
                bonk()
            }
        }
    }
    if (mySprite.isHittingTile(CollisionDirection.Right)) {
        if (playersprite_index == 3 || !(playersprite_index == 11)) {
            bonk()
        } else if (playersprite_index == 11 && 0 == player_isdoise_Coneguy) {
            if (!(player_ispepperman_Pixel)) {
                music.play(music.createSoundEffect(WaveShape.Square, 200, 1, 255, 0, 100, SoundExpressionEffect.None, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
                playerxscale = -1
                mySprite.vx = player_movespeed * playerxscale
            } else {
                bonk()
            }
        }
    }
}
